import styled from 'styled-components';
export default function AppTitle(props) {
  const { title = 'Movie Scout', subtitle = 'Your Movie Search Companion' } = props;
  return (
    <TitleWrapper>
      <h1>{title}</h1>
      <img src={"/movie.png"} alt="Movie Scout Logo" />
      <p>{subtitle}</p>
    </TitleWrapper>
  );
}

const TitleWrapper = styled.div`
  text-align: center;
  margin: 0 0 40px;
  h1 {
    color: ${({ theme }) => theme.mainColors.blue};
    letter-spacing: 10px;
    text-transform: uppercase;
    margin: 0 0 10px;
  }
  img {
    width: 200px; 
    height: 200px;
    margin: 0 0 10px;
  }
  p {
    color: ${({ theme }) => theme.mainColors.dark};
    margin: 0;
  }
`;
